CREATE DEFINER=`User`@`localhost` PROCEDURE `mostra_ingredienti`(
	IN codRicetta VARCHAR(45)
)
BEGIN
    -- Mostra gli ingredienti di una ricetta
    SELECT codIngrediente, nome, scadenza, Utilizza.quantitaRichiesta, limite, tipo
    FROM Ingrediente
    INNER JOIN Utilizza ON Utilizza.idIngrediente = Ingrediente.codIngrediente
	WHERE Utilizza.idRicetta = codRicetta;
END ;;
