CREATE DEFINER=`User`@`localhost` PROCEDURE `mostra_ricette`(
    IN categoria VARCHAR(45),
    IN provenienza VARCHAR(45),
    IN filtraggio VARCHAR(45),
    IN username VARCHAR(45)
)
BEGIN
    IF filtraggio = 'Tutte le ricette' THEN
        -- Seleziona tutte le ricette senza filtraggio
        SELECT codRicetta, titolo, immagine, cuoco, durataPreparazione, calorie, categoria
        FROM Ricetta
        WHERE categoria = Ricetta.categoria 
        AND provenienza = Ricetta.provenienza;
        
    ELSEIF filtraggio = 'Filtrate in base alla dispensa' THEN
        -- Seleziona solo le ricette che l'utente può fare in base agli ingredienti che possiede
        SELECT R.codRicetta, R.titolo, R.immagine, R.cuoco, R.durataPreparazione, R.calorie, R.categoria
        FROM Ricetta R
        WHERE R.categoria = categoria 
        AND R.provenienza = provenienza
        AND NOT EXISTS (
            -- Verifica che non ci siano ingredienti insufficienti o scaduti tra quelli che l'utente possiede
            SELECT 1
            FROM Utilizza U
            JOIN Ingrediente I ON U.idIngrediente = I.codIngrediente
            LEFT JOIN Possiede P ON P.idIngrediente = I.codIngrediente AND P.idUtente = username
            WHERE U.idRicetta = R.codRicetta
            AND (I.quantita < U.quantitaRichiesta OR I.scadenza <= CURDATE() OR P.idIngrediente IS NULL)
        );
    END IF;
END ;;
