CREATE DEFINER=`User`@`localhost` PROCEDURE `usa_ricetta`(
    IN codRicetta VARCHAR(45),
    OUT risultato BOOLEAN
)
BEGIN
    -- Dichiarazione variabili
    DECLARE numRigheAggiornate INT DEFAULT 0;
    DECLARE numIngredientiUtilizzati INT DEFAULT 0;
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN
        -- Gestisce eventuali errori impostando il risultato a FALSE
        SET risultato = FALSE;
        ROLLBACK;
    END;

    -- Avvia una transazione
    START TRANSACTION;
    
    -- Conta il numero di ingredienti utilizzati dalla ricetta
    SELECT COUNT(*) INTO numIngredientiUtilizzati
    FROM Utilizza
    WHERE idRicetta = codRicetta;

    -- Aggiorna la quantità degli ingredienti usati per la ricetta scelta
    UPDATE Ingrediente i
    INNER JOIN Utilizza u ON i.codIngrediente = u.idIngrediente
    SET i.quantita = i.quantita - u.quantitaRichiesta
    WHERE u.idRicetta = codRicetta;
    
    -- Controlla quante righe sono state aggiornate
    SET numRigheAggiornate = ROW_COUNT();
    
    -- Se tutte le righe attese sono state aggiornate, commit e risultato TRUE
    IF numRigheAggiornate = numIngredientiUtilizzati THEN
        COMMIT;
        SET risultato = TRUE;
    ELSE
        -- Se non tutte le righe sono state aggiornate, rollback e risultato FALSE
        ROLLBACK;
        SET risultato = FALSE;
    END IF;
END ;;
